package com.magento.inventory;

import com.fasterxml.jackson.annotation.JsonCreator;
import com.fasterxml.jackson.annotation.JsonProperty;
import java.lang.Integer;
import java.lang.String;

/**
 * Batch permits many stock-snapshot to be transfered as a single transaction
 * Batch information
 */
public final class SourceSnapshotBatch {
  /**
   * Universal Unique Identifier generated by the sender
   */
  @JsonProperty(
      value = "process_id",
      required = true
  )
  public final String processId;

  /**
   * Unique id of the message in the current batch
   */
  @JsonProperty(
      value = "process_number",
      required = true
  )
  public final Integer processNumber;

  /**
   * Total number of messages in the batch
   */
  @JsonProperty(
      value = "process_total",
      required = true
  )
  public final Integer processTotal;

  @JsonCreator
  public SourceSnapshotBatch(
      @JsonProperty(value = "process_id", required = true) final String processId,
      @JsonProperty(value = "process_number", required = true) final Integer processNumber,
      @JsonProperty(value = "process_total", required = true) final Integer processTotal) {
    this.processId = processId;
    this.processNumber = processNumber;
    this.processTotal = processTotal;
  }
}
